    private void addGActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_addGActionPerformed
                int maximo=100;
                double tiempo1=0,tiempo2=0;
                Burbuja b;
                BurbujaOptimizado bo;
              
                int muestras=30;
                int incremento=0;
                
                double[] bty = new double[muestras];
                double[] bmx = new double[muestras];
                double[] boty = new double[muestras];
                double[] bomx = new double[muestras];
                 
                for(int k=0;k<muestras;k++){
                    double[] datos =Generar.genera(incremento, maximo);
                    b=new Burbuja();
                    bo=new BurbujaOptimizado();
                    tiempo1=b.ordenar(incremento, datos);
                    tiempo2=bo.ordenar(incremento, datos);
                    bty[k]=tiempo1;
                    bmx[k]=incremento;
                    boty[k]=tiempo2;
                    bomx[k]=incremento;
                    incremento+=1000;
                }
                
		grafica.setTitulo("ComparaciÃ³n de rendimiento");
		grafica.crearGrafica("Burbuja", bmx, bty);
                grafica.agregarGrafica("BurbujaOptimizada", bomx, boty);
    }//GEN-LAST:event_addGActionPerformed

